<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>E-Pharmacy</title>
    <link rel="stylesheet" href="styles.css">
    <style>
        .gallery {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
            grid-gap: 20px;
            justify-items: center;
        }
        .gallery img {
            max-width: 100%;
            height: auto;
            border-radius: 8px;
            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
            transition: transform 0.3s ease;
            cursor: pointer;
        }
        .gallery img:hover {
            transform: scale(1.05);
        }
        .lightbox {
            display: none;
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.8);
            justify-content: center;
            align-items: center;
            z-index: 1000;
        }
        .lightbox img {
            width: 500px;
            height: 500px;
            border-radius: 8px;
            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
        }
        .lightbox .close, .lightbox .prev, .lightbox .next {
            position: absolute;
            color: white;
            font-size: 24px;
            background: rgba(0, 0, 0, 0.5);
            padding: 8px;
            cursor: pointer;
            border-radius: 50%;
        }
        .lightbox .close {
            top: 10px;
            right: 20px;
        }
        .lightbox .prev {
            left: 20px;
        }
        .lightbox .next {
            right: 20px;
        }
        .blur-background {
            filter: blur(8px);
        }
    </style>
</head>
<body>
    
    <header>
        <div class="logo">
            <h1>E-Pharmacy </h1>
        </div>
        <button class="menu-toggle" onclick="toggleMenu()">Menu</button>
        <nav>
            <ul>
                <li><a href="index.php">Home</a></li>                
                <li><a href="aboutus.php">About Us</a></li>
                <li><a href="gallery.php">Gallery</a></li>
                <li><a href="login.php">Login</a></li>
            </ul>
        </nav>
    </header>
    <div class="sidebar">
        <h2>Gallery</h2>
        <img class="rotate-img" src="https://w0.peakpx.com/wallpaper/370/305/HD-wallpaper-pharmacy-cute-pharmacy.jpg">
    </div>
    <main>
        <div class="container">
            <section class="content">
                <h2>E-Pharmacy Gallery Legends</h2>
                <p>Best medicines and health products are there.</p>

                <!-- Featured Products Section -->
                <section class="featured-products">
                    <div class="gallery">
                       
             <img src="data:image/jpeg;base64," alt="Product 1" onclick="openLightbox(0)">
             <img src="data:image/jpeg;base64," alt="Product 2" onclick="openLightbox(1)">
             <img src="data:image/jpeg;base64," alt="Product 3" onclick="openLightbox(2)">
             <img src="data:image/jpeg;base64," alt="Product 3" onclick="openLightbox(4)">
             <img src="data:image/jpeg;base64," alt="Product 3" onclick="openLightbox(3)">
             <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ6KsSFdbVFFvcoQWVOmwFUZy8b5xE_pImqVw&s" alt="Product 4" onclick="openLightbox(6)">
                    </div>
                </section>
            </section>
        </div>
    </main>

    <div class="lightbox" id="lightbox">
        <span class="close" onclick="closeLightbox()">&times;</span>
        <span class="prev" onclick="changeImage(-1)">&#10094;</span>
        <img id="lightbox-img" src="">
        <span class="next" onclick="changeImage(1)">&#10095;</span>
    </div>

    <footer>
        <div class="container">
            <p>&copy; 2024 E-Pharmacy. All rights reserved.</p>
        </div>
    </footer>

    <script>
        let currentImageIndex = 0;
        const images = document.querySelectorAll('.gallery img');
        const lightbox = document.getElementById('lightbox');
        const lightboxImg = document.getElementById('lightbox-img');

        function openLightbox(index) {
            currentImageIndex = index;
            lightboxImg.src = images[index].src;
            document.querySelector('main').classList.add('blur-background');
            lightbox.style.display = 'flex';
        }

        function closeLightbox() {
            lightbox.style.display = 'none';
            document.querySelector('main').classList.remove('blur-background');
        }

        function changeImage(direction) {
            currentImageIndex += direction;
            if (currentImageIndex < 0) {
                currentImageIndex = images.length - 1;
            } else if (currentImageIndex >= images.length) {
                currentImageIndex = 0;
            }
            lightboxImg.src = images[currentImageIndex].src;
        }
    </script>
</body>
</html>
